def gen_video():
    output_dir = "output/"
    out_path = os.path.join(output_dir, f"{filename(path)}ASS.mp4")
    ffmpeg_cmd = [
            "ffmpeg",
            "-i", path,
            "-vf", f"ass={ass_path}",
            "-c:a", "copy",
            "-y",
            out_path
    ]
    subprocess.run(ffmpeg_cmd, check=False)
    return out_path
        

def treat_tab():
    moyenne_time = 0
    moyenne_lenght = 0
    for j in tab:
        start = j[0]
        end = j[1]
        word= j[2]
        moyenne_time += (end-start)
        moyenne_lenght += len(word)
    moyenne_lenght = moyenne_lenght/len(tab)
    moyenne_time = moyenne_time/len(tab)
    
    retenue = 0
    seuil = 0.03
    for j in range(len(tab)):
        if retenue > 0:
            retenue -= 1
        else :
            retenue = juxtaposer_mots(tab, new_tab, seuil, j, moyenne_time, moyenne_lenght)

    
